      server { 
          listen 8188; 
          status_zone status-page; 

          allow 192.168.0.0/24; 
          deny all; 

          root /usr/share/nginx/html; 
          location = /status.html { } 
          location = / { 
              return 301 /status.html; 
          } 
          location /status { 
              status; 
              status_format json; 
          } 
      } 
